document.getElementById('subm').addEventListener('click',getcoordinates);
async function getcoordinates(){
    const city=document.getElementById('search').value;
    const url="http://api.openweathermap.org/geo/1.0/direct?q="+city+"&limit=1&appid="+api;
    try {
        const geoResponse = await fetch(url);
        const geoData = await geoResponse.json();
        if (geoData.length === 0) {
            alert('City not found');
            return;
        }
        const lati=geoData[0].lat;
        const long=geoData[0].lon;
        const country=geoData[0].country;
        const state=geoData[0].state;
        document.getElementById('city').innerHTML=`${city}`;
        getWeatherByCoordinates(lati, long,country,state);
    } catch (error) {
        console.error('Error fetching geocoding data:', error);
    }


}

async function getWeatherByCoordinates(lati, long,country,state) {
    const weatherUrl = `https://api.openweathermap.org/data/2.5/weather?lat=${lati}&lon=${long}&units=metric&appid=${api}`;
    document.getElementById('statecountry').innerHTML=`${state},${country}`;
    
    try {
        const weatherResponse = await fetch(weatherUrl);
        const weatherData = await weatherResponse.json();
        displayWeatherData(weatherData);
    } catch (error) {
        console.error('Error fetching weather data:', error);
    }
    const apiURL = `https://api.open-meteo.com/v1/forecast?latitude=${lati}&longitude=${long}&hourly=temperature_2m&daily=weather_code,temperature_2m_max,temperature_2m_min&timezone=GMT`;
 
    try {
        const weatherResponse = await fetch(apiURL);
        const weatherData = await weatherResponse.json();
        displayforecast(weatherData);
    } catch (error) {
        console.error('Error fetching weather data:', error);
    }

}
